import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDemoJson } from "../../store/actions/demoJsonActionCreators";
import axios from 'axios';

const ActualAPI = () => {
    const dispatch = useDispatch();
    const demoJson = useSelector( (store) => store.demoJson.data);

    const [value, setValue] = useState(1)

    const handleButtonClick = async () => {
        const result = await axios ({
            method: 'GET',
            //url: `https://jsonplaceholder.typicode.com/posts/${value}`

            url: `https://www.balldontlie.io/api/v1/teams/${value}`
        });

        console.log(result.data)

        setValue(value+1)
        dispatch(fetchDemoJson(result.data))
    }

    return (
        <div>
            <button
                type="button"
                onClick={handleButtonClick}
                id="button"
            >
                Click to learn about an NBA team!
            </button>

            <h1 id="team_name">{demoJson?.full_name}</h1>
            <p>City: {demoJson?.city}</p>
            <p>Conference: {demoJson?.conference}</p>
            <p>Division: {demoJson?.division}</p>
        </div>
    )
}

export default ActualAPI;
